import React, { useRef, useState } from "react";
import styles from "../styles/NewTXNPopup.module.css";
import ReactDom from "react-dom";
import Input_field from "./Input_field";
import classNames from "classnames";

export default function NewTXNPopup(props) {
  const [accounts, setAccounts] = useState(["Discover Savings", "Roth IRA"]);
  const [category, setCategory] = useState("");
  const [inputEmpty, setInputEmpty] = useState({});
  const inputRefs = useRef({});

  if (!props.displayNewTXN) return null;

  const subcategories = {
    Housing: [
      "Mortgage",
      "Rent",
      "Home/renters insurance",
      "Utilities",
      "Maintenance",
      "Misc",
    ],
    Transportation: [
      "Gas",
      "Car payment",
      "Car insurance",
      "Rideshare",
      "Misc",
    ],
    Food: ["Groceries", "Restaurants/Take-out", "Misc"],
    Bills: ["Electric", "Water", "Gas", "Misc"],
    Healthcare: ["Medical", "Dental", "Prescription", "Misc"],
    Entertainment: ["Hobby", "Games", "Travel", "Concerts", "Misc"],
    "Personal care": ["Hygiene products", "Haircut", "Spa/Salon", "Misc"],
    Clothing: ["Apparel", "Shoes", "Misc"],
    Education: [
      "Tuition",
      "Books",
      "School Supplies",
      "Tutoring/Online Help",
      "Misc",
    ],
    "Charitable donations": ["Non-profit", "Religious", "Misc"],
    Savings: [
      "IRA",
      "401k",
      "HRA/HSA",
      "529 Plan",
      "Bonds",
      "CDs",
      "Money-Market",
      "Mutual Funds",
      "ETFs",
      "Stocks",
    ],
    Misc: [],
  };

  const isEmpty = (key) => !inputRefs[key].value;

  const handleSubmit = (event) => {
    event.preventDefault();

    for (const refKey in inputRefs) {
      setInputEmpty((prevInputEmpty) => ({
        ...prevInputEmpty,
        [refKey]: isEmpty(refKey),
      }));
    }
  };

  return ReactDom.createPortal(
    <>
      <div className={styles.PopupBackground}></div>
      <div className={styles.TXNPopup}>
        <h1>New Transaction</h1>
        <hr />
        <form
          className={styles.new_txn_form}
          id="new_txn_form"
          onSubmit={handleSubmit}
        >
          <div>
            <label>*Account: </label>
            <select
              name="Account"
              id="Account"
              ref={(el) => (inputRefs["Account"] = el)}
              className={classNames({
                [styles.red_border]: inputEmpty["Account"],
              })}
            >
              <option isDisabled={true} value="">
                -- select an option --
              </option>
              {accounts.map((item) => (
                <option value={item}>{item}</option>
              ))}
            </select>
          </div>
          <div>
            <label>*Transaction Type: </label>
            <select
              name="Type"
              id="Type"
              ref={(el) => (inputRefs["Type"] = el)}
              className={classNames({
                [styles.red_border]: inputEmpty["Type"],
              })}
            >
              <option isDisabled={true} value="">
                -- select an option --
              </option>
              <option value="Income">Income</option>
              <option value="Expense">Expense</option>
              <option value="Transfer">Transfer</option>
              <option value="Refund">Refund</option>
              <option value="Investment">Investment</option>
            </select>
          </div>
          <div>
            <label>*Amount: </label>
            <input
              name="Amount"
              id="Amount"
              type="number"
              step="any"
              placeholder="enter amount"
              ref={(el) => (inputRefs["Amount"] = el)}
              className={classNames({
                [styles.red_border]: inputEmpty["Amount"],
              })}
            ></input>
          </div>

          {inputRefs["Type"].value === "Expense" ? (
            <div>
              <label>*Category: </label>
              <select
                name="Category"
                id="Category"
                onChange={(e) => {
                  setCategory(e.target.value);
                }}
                ref={(el) => (inputRefs["Category"] = el)}
                className={classNames({
                  [styles.red_border]: inputEmpty["Category"],
                })}
              >
                <option isDisabled={true} value="">
                  -- select an option --
                </option>
                <option value="Housing">Housing</option>
                <option value="Transportation">Transportation</option>
                <option value="Food">Food</option>
                <option value="Bills">Bills</option>
                <option value="Healthcare">Healthcare</option>
                <option value="Entertainment">Entertainment</option>
                <option value="Personal care">Personal care</option>
                <option value="Clothing">Clothing</option>
                <option value="Education">Education</option>
                <option value="Charitable donations">
                  Charitable donations
                </option>
                <option value="Savings">Savings</option>
                <option value="Misc">Misc</option>
              </select>
            </div>
          ) : null}

          <div>
            <label>*Subcategory: </label>
            <select
              name="Subcategory"
              id="Subcategory"
              ref={(el) => (inputRefs["Subcategory"] = el)}
              className={classNames({
                [styles.red_border]: inputEmpty["Subcategory"],
              })}
            >
              <option isDisabled={true} value="">
                -- select an option --
              </option>
              {category !== "" &&
                subcategories[category].map((item) => (
                  <option value={item}>{item}</option>
                ))}
            </select>
          </div>

          <div>
            <label>Description (optional): </label>
            <input
              name="Description"
              id="Description"
              type="text"
              ref={(el) => (inputRefs["Description"] = el)}
            ></input>
          </div>

          <div>
            <button type="submit">Submit</button>
          </div>
        </form>
      </div>
    </>,
    document.getElementById("newTXNPortal")
  );
}
