import "../styles/Navbar.css";
import { ReactComponent as PlusIcon } from "../icons/plus.svg";
import { ReactComponent as CogIcon } from "../icons/cog.svg";
import { ReactComponent as ChevronIcon } from "../icons/chevron.svg";
import { ReactComponent as ArrowIcon } from "../icons/arrow.svg";
import { ReactComponent as ProfileIcon } from "../icons/profile.svg";

import React, { useState, useEffect, useRef } from "react";

function Navbar() {
  const [dropdownOpen, setDropdownOpen] = useState(false);

  return (
    <Navbar_main>
      <NavItem icon={<PlusIcon />} setDropdownOpen={setDropdownOpen}>
        {dropdownOpen && <DropdownMenu></DropdownMenu>}
      </NavItem>

      <NavItem icon={<ProfileIcon />} />
    </Navbar_main>
  );
}

function Navbar_main(props) {
  return (
    <nav className="navbar">
      <ul className="navbar-nav">{props.children}</ul>
    </nav>
  );
}

function NavItem(props) {
  return (
    <li className="nav-item">
      <a
        href="#"
        className="icon-button"
        onMouseOver={() => props.setDropdownOpen(false)}
        onMouseOut={() => props.setDropdownOpen(false)}
      >
        {props.icon}
      </a>

      {props.children}
    </li>
  );
}

function DropdownMenu() {
  function DropdownItem(props) {
    return (
      <a href="#" className="menu-item">
        <span className="icon-button">{props.leftIcon}</span>
        {props.children}
        <span className="icon-right">{props.rightIcon}</span>
      </a>
    );
  }

  return (
    <div className="dropdown">
      <div className="menu">
        <DropdownItem leftIcon={<PlusIcon />}>New Account</DropdownItem>
        <DropdownItem leftIcon={<PlusIcon />}>New Transaction</DropdownItem>
      </div>
    </div>
  );
}

export default Navbar;
