import React from "react";
import { Line } from "react-chartjs-2";
import styles from "../styles/Chart.module.css";
import { Chart, registerables } from "chart.js";

Chart.register(...registerables);

const dateToString = (date) => {
  var year = date.getFullYear();
  var month = date.getMonth() + 1;
  var date = date.getDate();
  var time = month + "/" + date + "/" + year;
  return time;
};

const getDatesBetween = (start_date, end_date) => {
  const date_arr = [];
  for (let i = start_date; i <= end_date; i.setDate(i.getDate() + 1)) {
    date_arr.push(dateToString(i));
  }
  return date_arr;
};

const getRandomData = (arr_len) => {
  let data_arr = [];

  data_arr.push(Math.random() * 100);
  for (let i = 1; i < arr_len; i++) {
    let sign = Math.round(Math.random()) === 0 ? 1 : -1;
    data_arr.push(data_arr[i - 1] + Math.random() * 100 * sign);
  }
  return data_arr;
};

const label_arr = [
  "2022-01-01",
  "2022-02-01",
  "2022-03-01",
  "2022-04-01",
  "2022-05-01",
  "2022-06-01",
  "2022-07-01",
  "2022-08-01",
  "2022-09-01",
  "2022-10-01",
  "2022-11-01",
  "2022-12-01",
];

//getDatesBetween(new Date(2023, 0, 1), new Date(2023, 2, 3));
const data_arr = [12, 131, 15, 27, 10, 5, 31, 0, 9, 84, 88, 23];

//getRandomData(label_arr.length);

const data = {
  labels: label_arr,
  datasets: [
    {
      label: "Net Worth Tracker",
      backgroundColor: "rgb(255, 99, 132)",
      borderColor: "rgb(255, 99, 132)",
      data: data_arr,
    },
  ],
};

const options = {
  responsive: true,
  maintainAspectRatio: false,
  scales: {
    y: {
      ticks: { color: "white" },
    },
    x: {
      ticks: { color: "white" },
      type: "time",
      // time: {
      //   unit: "month",
      //   displayFormats: {
      //     month: "MMM YYYY",
      //   },
      // },
    },
  },
  plugins: {
    legend: {
      display: false,
      // labels: {
      //   color: "rgb(255, 99, 132)",
      // },
      // position: "right",
      // align: "center",
    },
  },
};

const LineChart = () => {
  return (
    <div className={styles.nw_chart}>
      <Line options={options} data={data} />
    </div>
  );
};

export default LineChart;
