import React, { useState } from "react";
import ReactDom from "react-dom";

import styles from "../styles/NewAccountPopup.module.css";
import classNames from "classnames";
import CardSelector from "./CardSelector";
import ColorPicker from "./ColorPicker";

const card_colors = [
  ["#ff0000", "#00ff00"],
  ["#0000ff", "#00ffff"],
];

const numLength = (num) => {
  return num.toString().replace(".", "").length;
};

export default function NewAccountPopup(props) {
  const [accountName, setAccountName] = useState("");
  const [accountBalance, setAccountBalance] = useState(0);
  const [cardColor, setCardColor] = useState(0);

  if (!props.displayNewAccount) return null;

  return ReactDom.createPortal(
    <>
      <div className={styles.PopupBackground}></div>
      <div className={styles.AccountPopup}>
        <div className={styles["container"]}>
          <h1 className={styles["header"]}>Style your new Card</h1>
          <button className={styles["close-btn"]} onClick={props.onClose}>
            x
          </button>

          <div className={styles["account-input"]}>
            <div className={styles["flex-col"]}>
              <label>Account name:</label>
              <input
                className={styles["account-info"]}
                placeholder="Enter account name"
                value={accountName}
                onChange={(e) => setAccountName(e.target.value)}
              />
            </div>

            <div className={styles["flex-col"]}>
              <label>Account balance:</label>
              <input
                type="number"
                className={styles["account-info"]}
                placeholder="Enter account balance"
                value={accountBalance}
                onChange={(e) =>
                  numLength(e.target.value) < 10
                    ? setAccountBalance(e.target.value)
                    : null
                }
              />
            </div>
          </div>

          <CardSelector
            accountName={accountName}
            accountBalance={accountBalance}
            color={cardColor}
          ></CardSelector>

          <div className={styles["color-palette"]}>
            <ColorPicker
              color={card_colors[0]}
              setColor={setCardColor}
            ></ColorPicker>
            <ColorPicker
              setColor={() => setCardColor(card_colors[1])}
            ></ColorPicker>
            {/* <ColorPicker setColor={() => setCardColor(card_colors[2])}></ColorPicker>
            <ColorPicker setColor={() => setCardColor(card_colors[3])}></ColorPicker>
            <ColorPicker setColor={() => setCardColor(card_colors[4])}></ColorPicker>
            <ColorPicker setColor={() => setCardColor(card_colors[5])}></ColorPicker>
            <ColorPicker setColor={() => setCardColor(card_colors[6])}></ColorPicker>
            <ColorPicker setColor={() => setCardColor(card_colors[7])}></ColorPicker>
            <ColorPicker setColor={() => setCardColor(card_colors[8])}></ColorPicker>
            <ColorPicker setColor={() => setCardColor(card_colors[9])}></ColorPicker>
            <ColorPicker setColor={() => setCardColor(card_colors[10])}></ColorPicker> */}
          </div>

          <button type="submit" className={styles["submit-button"]}>
            Submit
          </button>
        </div>
      </div>
    </>,
    document.getElementById("newAccountPortal")
  );
}
