import styles from "../styles/Chart.module.css";

import {
  Chart as chartJS,
  LineElement,
  TimeScale,
  LinearScale,
  PointElement,
  Tooltip,
  Legend,
} from "chart.js";

import "chartjs-adapter-date-fns";
import { Line } from "react-chartjs-2";

chartJS.register(
  LineElement,
  TimeScale,
  LinearScale,
  PointElement,
  Tooltip,
  Legend
);

const data = {
  labels: ["2022-11-01", "2022-11-02", "2022-11-03", "2022-11-04"],
  datasets: [
    {
      label: "369",
      data: [3, 6, 9, 3.69],
      backgroundColor: "aqua",
      borderColor: "black",
      tension: 0.4,
    },
  ],
};

const options = {
  layout: {
    padding: {
      bottom: 0,
    },
  },
  scales: {
    x: {
      type: "time",
      time: {
        unit: "day",
      },
    },
    y: {
      beginAtZero: true,
    },
  },
};

const LineChart = () => {
  return (
    <div className={styles["nw-chart"]}>
      <Line options={options} data={data} />
    </div>
  );
};

export default LineChart;
